import requests from "./requests"
import localforage from "localforage";

export async function getNotes() {
    let notes = await localforage.getItem("notes");
    if (!notes) notes = [];
    return notes;
}

function set(notes) {
    return localforage.setItem("notes", notes);
}

export async function getAllSerials() {
    let elem = await localforage.getItem("serials");
    requests.get('api/serials/serials/')
        .then((response) => {
            localforage.setItem("serials", response.data)
            return (elem)
        })
        .catch((response) => {
            console.log(response)
            return {error: "Can't get serials"}
        })
}
