import requests from "./requests"

export async function getAllSerials({sort, page, sortDirection, filter, filterField}) {
    let data = {
        page: page,
        sort: sort,
        sort_direction: sortDirection
    }
    if (filter && filterField) {
        data = Object.assign(data, {filter: filter, filter_field: filterField})
    }
    let result = await requests.get('api/serials/serials/', { params: data });
    if (result.status == 200){
        return result.data
    } else {
        return {error: "Can't load serials data"}
    }
}

export async function getSerial({serial_no}) {
    let data = {'serial_no': serial_no}
    let result = await requests.get('api/serials/serial/', { params:data });
    if (result.status == 200){
        return result.data
    } else {
        return {error: `Can't load serial id: ${serial_id}`}
    }
}

export async function getShops() {
    let data = {'page_size': 100000}
    let result = await requests.get('api/serials/shops', { params:data })
    if (result.status == 200){
        return result.data
    } else {
        return {error: "Can't load shops data"}
    }
}

export async function setSerialShopNo({serial_no, shopId}) {
    console.log(serial_no, shopId);
    let data = {
        serial_no: serial_no,
        shop_id: shopId
    }
    let result = await requests.post("api/serials/serial/", data)
    if (result.status == 200) {
        return result.data
    } else {
        return {error: "Can't set new shop for KSO"}
    }
}
