import { useState, useEffect } from "react";
import { Link, useNavigate, useParams } from "react-router-dom";
import { getSerial, getShops } from "../api";


export default function Serial() {
    const params = useParams();
    const [isLoading, setIsLoading] = useState(true);
    const [features, setFeatures] = useState({});
    const [serial, setSerial] = useState({});
    const [shop, setShop] = useState({});
    const [shopsDropdown, setShopsDropdown] = useState(false);
    const [shopList, setShopList] = useState([]);
    const serial_id = params.id;
    const navigate = useNavigate();
    useEffect(() => {
        if (isLoading) {
            setIsLoading(false);
            getSerial({ serial_id })
                .then((result) => {
                    console.log(result);
                    setFeatures(result.features);
                    setSerial(result);
                    let shop = result.shop;
                    Object.assign(shop, { "showingAddress": `${shop.shop_code}, ${shop.address}` });
                    console.log(shop)
                    setShop(shop);
                })
                .catch((error) => console.log(error))
        }
    })

    function getShops(event) {
        event.preventDefault();
        let data = new FormData(event.target);
        let filter = data.get("shop-input");
        getShops({filter})
            .then((result) => {
                console.log(result)
                // setShopList();
            })

    }

    function goBack(event) {
        event.preventDefault();
        return navigate('/serials')
    }

    function ShopForm() {
        function inputShop(event) {
            event.preventDefault();
            let data = new FormData(event.target);
        }
        return (

            <div className="mb-6 bg-grey-400">
                <label for="default-input" className="block mb-2 text-sm font-medium text-gray-900 dark:text-white">Магазин</label>
                <input type="text"  id="shop-input" className="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500"

                />
                {shopsDropdown ?
                    <div id="dropdown" className="z-10 hidden bg-white divide-y divide-gray-100 rounded-lg shadow w-44 dark:bg-gray-700">
                        <ul className="py-2 text-sm text-gray-700 dark:text-gray-200">
                            <li>
                                <a href="#" className="block px-4 py-2 hover:bg-gray-100 dark:hover:bg-gray-600 dark:hover:text-white">Dashboard</a>
                            </li>
                        </ul>
                    </div> : null
                }
            </div>
        )
    }

    return (
        <div className="relative overflow-x-auto shadow-md mt-2 ml-6 mr-2">
            <div className="flex flex-row items-center align-middle">
                <button onClick={goBack} className="text-gray-900 bg-white border border-gray-300 focus:outline-none hover:bg-gray-100 focus:ring-4 focus:ring-gray-200 font-medium rounded-lg text-sm px-5 py-2.5 me-2 mb-2 dark:bg-gray-800 dark:text-white dark:border-gray-600 dark:hover:bg-gray-700 dark:hover:border-gray-600 dark:focus:ring-gray-700">
                    Назад
                </button>
                <p className="text-black dark:text-white" >Серийный номер {serial.serial}</p>
            </div>
            <div className="mt-3 bg-grey-100 dark:bg-grey-600">
                <ShopForm />
            </div>
        </div>
    )
}
