import { useState, useEffect, useRef } from "react";
import { Link, useNavigate } from "react-router-dom";
import { getShops, getAllSerials } from "../api";
import GoBack from "./elements/goBack"

export default function Multitask() {
    const [shopId, setShopId] = useState(0);
    const [serials, setSerials] = useState({});

    function ShopForm() {
        const [shops, setShops] = useState([])
        const [shopList, setShopList] = useState([]);
        const [allShops, setAllShops] = useState([]);
        const [shopInput, setShopInput] = useState("");
        const [shopsDropdown, setShopsDropdown] = useState(false);


        useEffect(() => {
            if (shopList.length === 0) {
                getShops().then((result) => {
                    let shop_list = []
                    result.results.map((shop) => {
                        let s = {
                            label: `${shop.shop_code} ${shop.address}`,
                            value: shop.id,
                        }
                        shop_list.push(s)
                    })
                    setAllShops(shop_list);
                    setShopList(shop_list);
                })
            }
        }, [])

        function getShopsFilter(event) {
            let filter = event.target.value;
            let newValues = []
            allShops.forEach((shop) => {
                if (shop.label.includes(filter.toLowerCase())) {
                    newValues.push(shop);
                }
            })
            if (newValues.length === 0) {
                let empty = {
                    label: `Нет результатов`,
                    value: null,
                }
                newValues.push(empty)
            }
            setShopList(newValues);
            setShopsDropdown(true);
            setShopInput(filter);
            event.target.focus()
        }

        function setShopNo(id) {
            shopList.map((shop) => {
                if (shop.value == id) {
                    let value = shop.label;
                    setShopInput(value);
                    setShopId(id);
                    setShopsDropdown(false);

                }
            })
        }

        function ShopListElement({ label, value }) {
            return (
                <li key={value} onMouseDown={() => setShopNo(value)} >
                    <p className="shop-list-element">{label}</p>
                </li>
            )
        }
        return (

            <div>
                <label className="common-label"
                >Магазин</label>
                <input autoFocus
                    onBlur={() => setShopsDropdown(false)}
                    type="text"
                    onClick={() => setShopsDropdown(true)}
                    onChange={ getShopsFilter }
                    id="shop-input"
                    className="common-input"
                    placeholder="Добавить серийники выбранного магазина в список"
                    value={ shopInput }
                />
                {shopsDropdown ?
                    <div id="dropdown" className="absolute z-20 block w-full bg-white divide-y divide-gray-100 rounded-lg shadow w-44 dark:bg-gray-700">
                        <ul className="py-2 text-sm text-gray-700 dark:text-gray-200">
                            {shopList.map(shop =>
                                <ShopListElement label={shop.label} value={shop.value} key={shop.value} />)
                            }

                        </ul>
                    </div> : null
                }
            </div>
        )
    }

    function SerialsForm() {
        const [allSerials, setAllSerials] = useState({});
        const [filtredSerials, setFiltredSerials] = useState([]);
        const [showSerialsDropDown, setShowSerialsDropDown] = useState(false);    

        useEffect(() => {
            if (Object.keys(allSerials).length === 0) {
                let page_size = 10000;
                getAllSerials({ page_size }).then((result) => {
                    let newSerials = {};
                    result.results.forEach((serial) => {
                        newSerials[serial.serial] = serial
                    })
                    console.log(newSerials);
                    setAllSerials(newSerials);
                    setFiltredSerials(Object.values(newSerials));
                }).catch((error) => {
                    console.log(error);
                })
            }
        },[])

        function getSerialFilter(event) {
            event.preventDefault();
            let filter = event.target.value;
            let newValues = []
            Object.keys(allSerials).forEach((serial) => {
                if (serial.includes(filter.toLowerCase())) {
                    newValues.push(allSerials[serial]);
                }
            })
            if (newValues.length === 0) {
                let empty = {
                    serial: "Нет результатов",
                }
                newValues.push(empty);
            }
            setFiltredSerials(newValues);
            setShowSerialsDropDown(true);
        }

        function SerialInSearch({ value }) {
            let checked = false;
            let showCheckBox = false;
            if (Object.keys(serials).includes(value.toString())){
                checked = true;
            }
            if (Object.keys(allSerials).includes(value.toString())) {
                showCheckBox = true;
            }
            const [showCheckbox, setShowCheckbox] = useState(showCheckBox);
            const [check, setCheck] = useState(checked);

            function setSerial(event) {
                let s = serials;
                if (event.target.checked) {
                    s[value] = allSerials[value];
                    setCheck(true);
                } else {
                    delete s[value];
                    setCheck(false);
                }
                setSerials(s);
                setShowSerialsDropDown(true);
            }
            return (
                <li>
                    <div className="flex items-center ps-2 rounded hover:bg-gray-100 dark:hover:bg-gray-600">
                        { showCheckbox && 
                            <input id="checkbox-item-11" 
                                type="checkbox" 
                                onChange={setSerial} 
                                className="shop-list-checkbox" 
                                defaultChecked={check}
                            />
                        }
                        <label className="shop-list-label">{value}</label>
                    </div>
                </li>
            )
        }

        return (
            <div> 
                <label className="common-label">
                     Добавить серийные номера
                </label> 
                <label className="sr-only">Серийники</label>
                <div className="flex">
                    <button id="dropdown-button" 
                        className="arrow-serial-choice" 
                        type="button"
                        onClick={() => setShowSerialsDropDown(!showSerialsDropDown)}
                        >
                        <svg className="w-2.5 h-2.5 ms-2.5" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 10 6">
                            <path stroke="currentColor" strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="m1 1 4 4 4-4"/>
                    </svg></button>
                    <div className="relative w-full">
                        <input 
                            type="search" 
                            id="search-dropdown" 
                            className="h-12 block p-2.5 w-full text-sm text-gray-900 bg-gray-50 rounded-e-lg rounded-s-gray-100 rounded-s-2 border border-gray-300 focus:ring-blue-500 focus:border-blue-500 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:border-blue-500"
                            placeholder="Выбрать отдельные серийные номера" 
                            onClick={() => setShowSerialsDropDown(true)}
                            onChange={getSerialFilter}
                        />
                    </div>
                </div>
                    {showSerialsDropDown && 
                        <ul className="absolute w-full z-10 h-48 px-3 pb-3 bg-gray-50 border border-gray-300 text-gray-900 rounded-lg focus:ring-blue-500 focus:border-blue-500 dark:bg-gray-700 dark:border-gray-500 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500 overflow-y-auto text-sm text-gray-700 dark:text-gray-200" aria-labelledby="dropdownSearchButton">
                            {filtredSerials.forEach((serial) => { 
                                return <SerialInSearch key={`serial_${serial.serial}`} value={serial.serial} />}
                            )}
                        </ul>
                    }
            </div>

        )
    }

    function ChoicedSerials() {

        function SerialButton({serial}) {

            function onClick(event){
                let s = serial.serial;
                let ser = serials;
                delete ser[s];
                setSerials(ser);
                event.preventDefault();
            }

            return (
                <button 
                    onClick={onClick}
                    className="serial-choiced"
                >
                    {serial.serial} x
                </button>
            )
        }

        return(
            <div>
                <label className="common-label">
                    Выбранные серийные номера
                </label> 
                <div className="h-12 common-input grid grid-cols-8 gap-2 p-1">
                {
                    Object.keys(serials).map((serial) => 
                        {
                            return <SerialButton serial={serials[serial]} key={`choised_${serial}`}/>
                        }
                    )
                }
                </div>
            </div>
        )
    }

    return (
        <div className="relative overflow-x-auto shadow-md">
            <GoBack text="Задачи" />
            <form className="mb-4 border border-gray-200 bg-gray-100 dark:bg-gray-500 dark:border-gray-700 rounded-lg p-2">
                <ShopForm />
                <SerialsForm />
                <ChoicedSerials/>
            </form>
            {Object.keys(serials).map((serial) => 
                <p>{serial}</p>
                )}
        </div>
    )
}
