import { useState, useEffect } from "react";
import { Link, useNavigate, useParams } from "react-router-dom";
import { getSerial, getShops, setSerialShopNo, changeSerialNo } from "../api";
import feature_false from "../images/feature-false.png"
import feature_true from "../images/feature-true.png"


export default function Serial() {
    const params = useParams();
    const [isLoading, setIsLoading] = useState(true);
    const [features, setFeatures] = useState([]);
    const [serial, setSerial] = useState({ serial: 0, });
    const [shopInput, setShopInput] = useState("");
    const [shopsDropdown, setShopsDropdown] = useState(false);
    const [shopList, setShopList] = useState([]);
    const [shopAccept, setShopAccept] = useState(false);
    const [shopError, setShopError] = useState(false);
    const serial_no = params.serial;
    const navigate = useNavigate();
    const [shopId, setShopId] = useState(0);
    const [allShops, setAllShops] = useState([]);
    const [changeSerialError, setChangeSerialError] = useState(false);
    const [changeSerialExist, setChangeSerialExist] = useState(false);
    useEffect(() => {
        if (isLoading) {
            setIsLoading(false);
            getSerial({ serial_no })
                .then((result) => {
                    result.features.map((feature) => {
                        if (!features.includes(feature)){
                            features.push(feature)
                        }

                    });
                    console.log(features)
                    Object.keys(result).map((key) => {
                        serial[key] = result[key]
                    });
                    let shop = result.shop;
                    let shop_input = `${shop.shop_code} ${shop.address}`;
                    setShopId(shop.id)
                    setShopInput(shop_input);
                })
                .catch((error) => {
                    console.log(error);
                    navigate('/serials');
                })
        }
        if (shopList.length === 0) {
            getShops().then((result) => {
                let shop_list = []
                result.results.map((shop) => {
                    let s = {
                        label: `${shop.shop_code} ${shop.address}`,
                        value: shop.id,
                    }
                    shop_list.push(s)
                })
                setAllShops(shop_list)
                setShopList(allShops);
            })
        }
    })

    function getShopsFilter(event) {
        let filter = event.target.value;
        let newValues = []
        allShops.forEach((shop) => {
            if (shop.label.includes(filter.toLowerCase())) {
                newValues.push(shop);
            }
        })
        if (newValues.length === 0) {
            let empty = {
                label: `Нет результатов`,
                value: null,
            }
            newValues.push(empty)
        }
        setShopList(newValues);
        setShopsDropdown(true);
        setShopInput(filter);
        event.target.focus()
    }

    function setShopNo(id) {
        shopList.map((shop) => {
            if (shop.value == id) {
                let value = shop.label;
                setShopInput(value);
                setShopId(id);
                setShopsDropdown(false);
            }
        })
    }

    function submitShopNo(event) {
        event.preventDefault();
        let serial_id = serial.id;
        setSerialShopNo({ serial_id, shopId })
            .then((result => {
                Object.keys(result).map((key) => {
                    serial[key] = result[key]
                });
                setShopAccept(true);
                setShopError(false);
            })).catch((error) => {
                console.log(error);
                setShopAccept(false);
                setShopError(true);
            })

    }

    function submitChangeSerial(event) {
        event.preventDefault();
        let data = new FormData(event.target)
        let serial_new = data.get("new_serial");
        let serial_id = serial.id
        changeSerialNo({ serial_id, serial_new }).then(
            (result) => {
                setChangeSerialExist(false);
                setChangeSerialError(false);
                serial.serial = serial_new;
                navigate(`/serials/${serial_new}`)
            }
        ).catch(
            (error) => {
                if (error.response.status === 403) {
                    setChangeSerialExist(true);
                    setChangeSerialError(false);
                } else {
                    setChangeSerialExist(false);
                    setChangeSerialError(true);
                }
            }
        )
    }

    function submitFeatures(event) {
        event.preventDefault();
    }

    function goBack(event) {
        event.preventDefault();
        return navigate('/serials')
    }

    function ShopListElement({ label, value }) {
        return (
            <li key={value} onMouseDown={() => setShopNo(value)} >
                <p className="px-4 py-2 hover:bg-gray-100 dark:hover:bg-gray-600 dark:hover:text-white">{label}</p>
            </li>
        )
    }

    function SerialForm() {
        return (<form onSubmit={submitChangeSerial} className="mb-4  bg-white dark:bg-gray-500 rounded-lg p-2">
            <div>
                <label className="block mb-2 text-sm font-medium text-gray-900 dark:text-white">Серийный номер</label>
                <input type="number"
                    id="new_serial"
                    name="new_serial"
                    placeholder={serial.serial}
                    className="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500"
                    required />
                {changeSerialError ? <label className="text-red-600 dark:text-red-500">Что-то пошло не так</label> : null}
                {changeSerialExist ? <label className="text-red-600 dark:text-red-500">Такой номер занят</label> : null}
                <div className="flex justify-end mt-2 mb-0">
                    <button
                        type="submit"
                        className="text-gray-900 bg-white border border-gray-300 focus:outline-none hover:bg-gray-100 focus:ring-4 focus:ring-gray-200 font-medium rounded-lg text-sm px-5 py-2.5 me-2 mb-2 dark:bg-gray-800 dark:text-white dark:border-gray-600 dark:hover:bg-gray-700 dark:hover:border-gray-600 dark:focus:ring-gray-400">Сменить серийный номер</button>
                </div>
            </div>
        </form>)
    }

    function ShopForm({ list, labelName }) {
        return (
            <form onSubmit={submitShopNo} className="mb-4  bg-white dark:bg-gray-500 rounded-lg p-2">
                <div>
                    <label className="block mb-2 text-sm font-medium text-gray-900 dark:text-white">{labelName}</label>
                    <input autoFocus onBlur={() => setShopsDropdown(false)} type="text" onClick={() => setShopsDropdown(true)} onChange={getShopsFilter} id="shop-input" className="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500"
                        value={shopInput}
                    />
                    {shopsDropdown ?
                        <div id="dropdown" className="absolute z-10 block w-full bg-white divide-y divide-gray-100 rounded-lg shadow w-44 dark:bg-gray-700">
                            <ul className="py-2 text-sm text-gray-700 dark:text-gray-200">
                                {list.map(shop =>
                                    <ShopListElement label={shop.label} value={shop.value} key={shop.value} />)
                                }

                            </ul>
                        </div> : null
                    }
                </div>
                {shopAccept ? <label className="font-medium text-purple-800">Магазин сохранен</label> : null}
                {shopError ? <label className="font-medium text-red-600">Не удалось сохранить магазин</label> : null}
