import requests from "./requests"

export async function getAllSerials({sort, page, sortDirection}) {
    let data = {
        page: page,
        sort: sort,
        sort_direction: sortDirection
    }
    let result = await requests.get('api/serials/serials/', { params: data });
    console.log(result)
    if (result.status == 200){
        return result.data
    } else {
        return {error: "Can't load serials data"}
    }
}
