import { useState } from "react";
import { Link, useLoaderData } from "react-router-dom";

export default function Srials() {
    // const {data} = useLoaderData();
    // console.log(data);
    const [serials] = useState([
        {
            "serial": 1005,
            "client": null,
            "shop": null
        },
        {
            "serial": 1001,
            "client": null,
            "shop": 2
        },]);
    // const [pages, setPages] = useState(0);
    // const [page, setPage] = useState(0);
    const dataName = {
        serial: { name: 'Серийный номер', sort: false },
        client: { name: 'Клиент', sort: true },
        shop: { name: 'Номер магазина', sort: true },
        mac: { name: 'MAC адрес', sort: false },
        cpu_name: { name: 'Имя процессора', sort: false },
        os: { name: 'Операционная система', sort: false },
        // usb_devices: {name: 'Серийный номер', sort: false},
        screen_resolution: { name: 'Разрешение экрана', sort: false },
        ip: { name: 'ip адрес', sort: false },
        hostname: { name: 'Имя хоста', sort: true },
        // accept_serial: {name: 'Серийный номер', sort: false},
        last_ping: { name: 'Онлайн', sort: true },
    };

    function Header({ data }) {
        return (
            <th scope="col" class="px-6 py-3">
                <div class="flex items-center ">
                    {data.name}
                    {data.sort ? <a href="#"><svg class="w-3 h-3 ms-1.5" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="currentColor" viewBox="0 0 24 24">
                        <path d="M8.574 11.024h6.852a2.075 2.075 0 0 0 1.847-1.086 1.9 1.9 0 0 0-.11-1.986L13.736 2.9a2.122 2.122 0 0 0-3.472 0L6.837 7.952a1.9 1.9 0 0 0-.11 1.986 2.074 2.074 0 0 0 1.847 1.086Zm6.852 1.952H8.574a2.072 2.072 0 0 0-1.847 1.087 1.9 1.9 0 0 0 .11 1.985l3.426 5.05a2.123 2.123 0 0 0 3.472 0l3.427-5.05a1.9 1.9 0 0 0 .11-1.985 2.074 2.074 0 0 0-1.846-1.087Z" />
                    </svg></a> : null}

                </div>
            </th>
        )
    }

    function HeaderRow() {
        return (
            <thead class="text-xs text-gray-700 uppercase bg-gray-50 dark:bg-gray-700 dark:text-gray-400">
                <tr>
                    {Object.keys(dataName).map(data => 
                        <Header data={dataName[data]} />
                    )}
                </tr>
            </thead>
        )
    }

    function Cell({ text , link }) {
        var element = null;
        if (link != null){
            const url = "serials/" + link + "/";
            element = <th class="px-6 py-3 font-medium text-gray-900 whitespace-nowrap dark:text-white"><Link to={ url } > { text }</Link></th>;
        } else {
            element = <td class="px-6 py-3">{ text ? text : "--" }</td>;
        }
        return ( element )
    }

    function Row({ data }) {
        console.log(data);
        return (
            <tr class="bg-white border-b dark:bg-gray-800 dark:border-gray-700">
                {Object.keys(dataName).map( key => 
                    <Cell text={data[key]} link={key==="serial" ? data['id']:null}/>
                )}
            </tr>
        )

    }

    function Rows() {
        console.log(serials)
        return (
            serials.map(serial => 
                <Row data={ serial } /> 
            )
        )
    }

    return (
        <div class="relative flex-nowrap shadow-md sm:rounded-lg ml-7 mr-2 ">
            <table class=" text-sm text-left rtl:text-right text-gray-500 dark:text-gray-400">
                <tbody>
                    <HeaderRow />
                    <Rows />
                </tbody>
            </table>
        </div>
    )
}
