import requests from "./requests"
import localforage from "localforage";

export async function getSerials() {
    let notes = await localforage.getItem("serials");
    if (!notes) notes = [];
    return notes;
}

function set(notes) {
    return localforage.setItem("serials", notes);
}

export async function getAllSerials() {
    let elem = await getSerials();
    requests.get('api/serials/serials/')
        .then((response) => {
            elem.unshift(response.data)
            localforage.setItem("serials", elem)
            return (elem)
        })
        .catch((response) => {
            console.log(response)
            return {error: "Can't get serials"}
        })
}
