import { useState, useEffect, useRef } from "react";
import { Link, useNavigate } from "react-router-dom";
import { getShops, getAllSerials } from "../api";
import GoBack from "./elements/goBack"

export default function Multitask() {
    const [shopId, setShopId] = useState(0);
    const serialFormRef = useRef(null);

    useEffect(() => {
        const handleClick = event => {
            console.log(event)
        };
        const element = serialFormRef.current;
        if (element){
        element.addEventListener('click', handleClick);
        return () => {
            element.removeEventListener('click', handleClick);
          };}
    })

    function ShopForm() {
        const [shopList, setShopList] = useState([]);
        const [allShops, setAllShops] = useState([]);
        const [shopInput, setShopInput] = useState("");
        const [shopsDropdown, setShopsDropdown] = useState(false);


        useEffect(() => {
            if (shopList.length === 0) {
                getShops().then((result) => {
                    let shop_list = []
                    result.results.map((shop) => {
                        let s = {
                            label: `${shop.shop_code} ${shop.address}`,
                            value: shop.id,
                        }
                        shop_list.push(s)
                    })
                    setAllShops(shop_list)
                    setShopList(allShops);
                })
            }
        })

        function getShopsFilter(event) {
            let filter = event.target.value;
            let newValues = []
            allShops.forEach((shop) => {
                if (shop.label.includes(filter.toLowerCase())) {
                    newValues.push(shop);
                }
            })
            if (newValues.length === 0) {
                let empty = {
                    label: `Нет результатов`,
                    value: null,
                }
                newValues.push(empty)
            }
            setShopList(newValues);
            setShopsDropdown(true);
            setShopInput(filter);
            event.target.focus()
        }

        function setShopNo(id) {
            shopList.map((shop) => {
                if (shop.value == id) {
                    let value = shop.label;
                    setShopInput(value);
                    setShopId(id);
                    setShopsDropdown(false);
                }
            })
        }

        function ShopListElement({ label, value }) {
            return (
                <li key={value} onMouseDown={() => setShopNo(value)} >
                    <p className="px-4 py-2 hover:bg-gray-100 dark:hover:bg-gray-600 dark:hover:text-white">{label}</p>
                </li>
            )
        }
        return (

            <div>
                <label className="block mb-2 text-sm font-medium text-gray-900 dark:text-white"
                >Магазин</label>
                <input autoFocus
                    onBlur={() => setShopsDropdown(false)}
                    type="text"
                    onClick={() => setShopsDropdown(true)}
                    onChange={getShopsFilter}
                    id="shop-input"
                    className="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500"
                    placeholder="Добавить серийники выбранного магазина в список"
                    value={shopInput}
                />
                {shopsDropdown ?
                    <div id="dropdown" className="absolute z-10 block w-full bg-white divide-y divide-gray-100 rounded-lg shadow w-44 dark:bg-gray-700">
                        <ul className="py-2 text-sm text-gray-700 dark:text-gray-200">
                            {shopList.map(shop =>
                                <ShopListElement label={shop.label} value={shop.value} key={shop.value} />)
                            }

                        </ul>
                    </div> : null
                }
            </div>
        )
    }

    function SerialsForm( ref ) {
        const [allSerials, setAllSerials] = useState({});
        const [showSerialsDropDown, setShowSerialsDropDown] = useState(false);
        useEffect(() => {
            if (Object.keys(allSerials).length === 0) {
                let page_size = 10000;
                getAllSerials({ page_size }).then((result) => {
                    result.results.map((serial) => {
                        allSerials[serial.serial] = serial
                    })
                    console.log(allSerials)
                })
            }
        })
        function SerialInSearch({ value }) {
            return (
                <li>
                    <div className="flex items-center ps-2 rounded hover:bg-gray-100 dark:hover:bg-gray-600">
                        <input id="checkbox-item-11" type="checkbox" value="" className="w-4 h-4 text-blue-600 bg-gray-100 border-gray-300 rounded focus:ring-blue-500 dark:focus:ring-blue-600 dark:ring-offset-gray-700 dark:focus:ring-offset-gray-700 focus:ring-2 dark:bg-gray-600 dark:border-gray-500" />
                        <label className="w-full py-2 ms-2 text-sm font-medium text-gray-900 rounded dark:text-gray-300">{value}</label>
                    </div>
                </li>
            )
        }

        return (
            <div  onClick={() => setShowSerialsDropDown(true)} ref={ref}> 
                <label className="block mb-2 text-sm font-medium text-gray-900 dark:text-white">
                    Серийные номера
                </label>
                <input autoFocus
                    type="text"


                    className="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500"
                    placeholder="Добавить серийники выбранного магазина в список"
                />
                {showSerialsDropDown && 
                <div id="dropdownSearch" className="z-10 bg-white rounded-lg shadow w-full dark:bg-gray-700">
                    <div className="p-3">
                        <label className="sr-only">Серийники</label>
                        <div className="relative">
                            <div className="absolute inset-y-0 rtl:inset-r-0 start-0 flex items-center ps-3 pointer-events-none">
                                <svg className="w-4 h-4 text-gray-500 dark:text-gray-400" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 20 20">
                                    <path stroke="currentColor" strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="m19 19-4-4m0-7A7 7 0 1 1 1 8a7 7 0 0 1 14 0Z" />
                                </svg>
                            </div>
                            <input type="text" id="input-group-search" className="block w-full p-2 ps-10 text-sm text-gray-900 border border-gray-300 rounded-lg bg-gray-50 focus:ring-blue-500 focus:border-blue-500 dark:bg-gray-600 dark:border-gray-500 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500" placeholder="Search user" />
                        </div>
                    </div>
                    <ul className="h-48 px-3 pb-3 overflow-y-auto text-sm text-gray-700 dark:text-gray-200" aria-labelledby="dropdownSearchButton">
                        {Object.keys(allSerials).map((serial) => 
                            <SerialInSearch value={serial} />
                        )}
                    </ul>
                    <a className="flex items-center p-3 text-sm font-medium text-red-600 border-t border-gray-200 rounded-b-lg bg-gray-50 dark:border-gray-600 hover:bg-gray-100 dark:bg-gray-700 dark:hover:bg-gray-600 dark:text-red-500 hover:underline">
                        <svg className="w-4 h-4 me-2" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="currentColor" viewBox="0 0 20 18">
                            <path d="M6.5 9a4.5 4.5 0 1 0 0-9 4.5 4.5 0 0 0 0 9ZM8 10H5a5.006 5.006 0 0 0-5 5v2a1 1 0 0 0 1 1h11a1 1 0 0 0 1-1v-2a5.006 5.006 0 0 0-5-5Zm11-3h-6a1 1 0 1 0 0 2h6a1 1 0 1 0 0-2Z" />
                        </svg>
                        Delete user
                    </a>
                </div>}
            </div>

        )
    }

    return (
        <div className="relative overflow-x-auto shadow-md">
            <GoBack text="Задачи" />
            <form className="mb-4 border border-gray-200 bg-gray-100 dark:bg-gray-500 dark:border-gray-700 rounded-lg p-2">
                <ShopForm />
                <SerialsForm ref={serialFormRef}/>
            </form>
        </div>
    )
}
