import { useState, useEffect } from "react";
import { Link, useNavigate, useParams } from "react-router-dom";
import { getSerial, getShops } from "../api";
import Dropdown from '@gilbarbara/react-dropdown';


export default function Serial() {
    const params = useParams();
    const [isLoading, setIsLoading] = useState(true);
    const [features, setFeatures] = useState({});
    const [serial, setSerial] = useState({});
    const [shopInput, setShopInput] = useState({});
    const [shopsDropdown, setShopsDropdown] = useState(false);
    const [shopList, setShopList] = useState([]);
    const serial_id = params.id;
    const navigate = useNavigate();
    useEffect(() => {
        if (isLoading) {
            setIsLoading(false);
            getSerial({ serial_id })
                .then((result) => {
                    setFeatures(result.features);
                    setSerial(result);
                    let shop = result.shop;
                    let shop_input = `${shop.shop_code}, ${shop.address}`;
                    setShopInput(shop_input);
                })
                .catch((error) => console.log(error))
        }
    })

    function getShopsFilter(event) {
        console.log(event);
        let filter = event.target.value;
        console.log(filter);
        // setShopInput(filter);
        getShops({filter})
            .then((result) => {
                console.log(result)
                // setShopList();
            })

    }

    function goBack(event) {
        event.preventDefault();
        return navigate('/serials')
    }

    function ShopForm() {
        return (

            <div className="mb-6 bg-grey-400">
                <label className="block mb-2 text-sm font-medium text-gray-900 dark:text-white">Магазин</label>

                <Dropdown onChange={(value) => getShopsFilter(value)} options={shopList} />

            </div>
        )
    }

    return (
        <div className="relative overflow-x-auto shadow-md mt-2 ml-6 mr-2">
            <div className="flex flex-row items-center align-middle">
                <button onClick={goBack}  className="text-gray-900 bg-white border border-gray-300 focus:outline-none hover:bg-gray-100 focus:ring-4 focus:ring-gray-200 font-medium rounded-lg text-sm px-5 py-2.5 me-2 mb-2 dark:bg-gray-800 dark:text-white dark:border-gray-600 dark:hover:bg-gray-700 dark:hover:border-gray-600 dark:focus:ring-gray-700">
                    Назад
                </button>
                <p className="text-black dark:text-white" >Серийный номер {serial.serial}</p>
            </div>
            <div className="mt-3 bg-grey-100 dark:bg-grey-200">
                <ShopForm />
            </div>
        </div>
    )
}
