import { useState, useEffect } from "react";
import { Link, useNavigate, useParams } from "react-router-dom";
import { getSerial, getShops } from "../api";
import Dropdown from '@gilbarbara/react-dropdown';



export default function Serial() {
    const params = useParams();
    const [isLoading, setIsLoading] = useState(true);
    const [features, setFeatures] = useState({});
    const [serial, setSerial] = useState({});
    const [shopInput, setShopInput] = useState({});
    const [shopsDropdown, setShopsDropdown] = useState(false);
    const [shopList, setShopList] = useState([]);
    const serial_id = params.id;
    const navigate = useNavigate();
    var shopId = null;
    useEffect(() => {
        if (isLoading) {
            setIsLoading(false);
            getSerial({ serial_id })
                .then((result) => {
                    console.log(result);
                    setFeatures(result.features);
                    setSerial(result);
                    let shop = result.shop;
                    let shop_input = `${shop.shop_code} ${shop.address}`;
                    shopId = shop.id
                    setShopInput(shop_input);
                })
                .catch((error) => console.log(error))
        }
        if (shopList.length === 0) {
            getShops({shopId}).then((result) => {
                let shop_list = []
                result.results.map((shop)=>{
                    let s = {
                        label: `${shop.shop_code} ${shop.address}`,
                        value: shop.id,
                    }
                    shop_list.push(s)
                })
                setShopList(shop_list);
            })
        }
    })

    function getShopsFilter(event) {
        let filter = event.target.value;
        event.target.focus()
        getShops({ filter })
            .then((result) => {
                console.log(result)
                setShopList(result.results);
                setShopsDropdown(true);
                setShopInput(filter);
            })
        event.target.focus()
        event.target.value = filter

    }

    function setShopNo({ id }) {
        console.log(id);
        shopList.map((shop) => {
            if (shop.id == id) {
                let value = `${shop.shop_code} ${shop.address}`;
                setShopInput(value);
                shopId = id;
                setShopsDropdown(false);
            }
        })
    }

    function goBack(event) {
        event.preventDefault();
        return navigate('/serials')
    }

    function ShopListElement({ shop }) {
        return (
            <li key={shop.id} onMouseDown={()=>setShopNo(shop)} >
                <p className="px-4 py-2 hover:bg-gray-100 dark:hover:bg-gray-600 dark:hover:text-white">{shop.shop_code} {shop.address}</p>
            </li>
        )
    }

    function ConctentComponent(){
        return(
            <div className="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500"></div>
        )
    }

    function InputComponent(){
        return(
            <input autoFocus className="bg-gray-50 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500"/>
        )
    }

    function MenuItemComponent(props){
        let methods = props.methods
        let item = props.item
        return(
            <div value={item.value} className="px-4 py-2 hover:bg-gray-100 dark:hover:bg-gray-600 dark:hover:text-white" >{item.label}</div>
        )
    }

    function MenuComponent(props){
        console.log(props)
        return (
            <div></div>
        )
    }

    function ShopForm() {
        const mystyle = {
            "background-color": "#374151",
            "border-color": '#4b5563'
        }
        return (
            <div className="mb-6  bg-white dark:bg-gray-500 rounded-lg p-2">
                <label className="block mb-2 text-sm font-medium text-gray-900 dark:text-white">Магазин</label>
                <Dropdown options={shopList} 
                    style={mystyle}
                    className="bg-dark-900 dark:bg-gray-500 p-0"
                    menuComponent={MenuComponent}
/>
                {/* <input autoFocus onBlur={()=>setShopsDropdown(false)} type="text" onChange={getShopsFilter} id="shop-input" className="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500"
                    value={shopInput}
                />
                {shopsDropdown ?
                    <div id="dropdown" className="absolute z-10 block w-full bg-white divide-y divide-gray-100 rounded-lg shadow w-44 dark:bg-gray-700">
                        <ul className="py-2 text-sm text-gray-700 dark:text-gray-200">
                            {shopList.map(shop =>
                                <ShopListElement shop={shop} />)
                            }

                        </ul>
                    </div> : null
                } */}
            </div>
        )
    }

    return (
        <div className="relative overflow-x-auto shadow-md mt-2 ml-6 mr-2">
            <div className="flex flex-row items-center align-middle">
                <button onClick={goBack} className="text-gray-900 bg-white border border-gray-300 focus:outline-none hover:bg-gray-100 focus:ring-4 focus:ring-gray-200 font-medium rounded-lg text-sm px-5 py-2.5 me-2 mb-2 dark:bg-gray-800 dark:text-white dark:border-gray-600 dark:hover:bg-gray-700 dark:hover:border-gray-600 dark:focus:ring-gray-700">
                    Назад
                </button>
                <p className="text-black dark:text-white" >Серийный номер {serial.serial}</p>
            </div>
            <div className="mt-3 bg-grey-100 dark:bg-grey-200">
                <ShopForm />
                <p>
                    a
                </p>
                <p>a</p>
                <p>f</p>
            </div>
        </div>
    )
}
